func main() {
	config.LoadDefaults()

	categories := []model.Category{model.Category{Name: "水果", HTML: htmlstring1}, model.Category{Name: "蔬菜", HTML: htmlstring2}, model.Category{Name: "畜牧水产", HTML: htmlstring3}, model.Category{Name: "粮油米面", HTML: htmlstring4}, model.Category{Name: "农副加工", HTML: htmlstring5}, model.Category{Name: "苗木花草", HTML: htmlstring6}}
	manager := mongo.NewCagegoryManager()
	err := manager.RemoveAll()
	if err != nil {
		panic(err)
	}
	for i := range categories {
		categories[i].Parent = ""
		categories[i].CreatedAt = time.Now()
		category, err := manager.Insert(categories[i])
		if err != nil {
			panic(err)
		}
		println("first:" + category.Name + " \n")
		reader := strings.NewReader(categories[i].HTML)
		dom, err := goquery.NewDocumentFromReader(reader)
		if err != nil {
			panic(err)
		}
		var seq = int16(0)
		dom.Find(".list-t a.sub").Each(func(i int, s *goquery.Selection) {
			seq++
			sub, err := manager.Insert(model.Category{Name: s.Text(), Seq: seq, Parent: category.ID.Hex(), CreatedAt: time.Now()})
			if err != nil {
				panic(err)
			}
			nID, _ := s.Attr("id")
			childNodeID := strings.Replace(nID, "a", "div", -1)
			println("sub:" + sub.Name + "," + nID + "," + childNodeID + " \n")
			var childseq = int16(0)
			dom.Find(fmt.Sprintf("#%s a", childNodeID)).Each(func(i int, s *goquery.Selection) {
				childseq++
				child, err := manager.Insert(model.Category{Name: s.Text(), Seq: childseq, Parent: sub.ID.Hex(), CreatedAt: time.Now()})
				if err != nil {
					panic(err)
				}
				println("child:" + child.Name + " \n")
			})
		})
	}

}
